mapper.select:
    	SELECT
			IFNULL(TIMESTAMPDIFF(SECOND,MIN(record_time),MAX(record_time)),0)  AS timed
		FROM znzz_waiting_record
		WHERE machine_id =#{id}
		AND DATE(record_time) =#{str}

SELECT machine_name as sbmc, maintenance_signal as whxh, partsCount_target as jgmbz, partsCount_result as jgssz, override_rapid as kjbl, override_spindle as zzbl, override_feed as qxbl, spindle_mode as zzms, record_time as jlss FROM znzz_waiting_record where id > 0
AND DATE(record_time) BETWEEN #{filter.startTime} AND #{filter.endTime};
AND machine_name=#{filter.name}
